/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

:root{
  /* Font Size */
  --font-size-test: 0.75rem;
  --font-size-extralarge: 3.5rem;
  --font-size-large: 2.3rem;
  --font-size-medium: 1.5rem;
  --font-size-normal: 18px;
  --font-size-small: 0.75rem;

  /* Font Weight */
  --font-weight-normal: 400;
  --font-weight-light: 200;
  --font-weight-medium: 500;
  --font-weight-bold: 700;
  --font-weight-black: 900;

  /* Line height */
  --leading-none: 100%;
  --leading-normal: 120%;
  --leading-relaxed: 160%;

  /* Gap */
  --gap-small: 20px;
  --gap-medium: 40px;
  --gap-large: 80px;

  /* Primary Colors */
  --primary-color: black; 
  --primary-dark: #0056b3; 
  --primary-light: #4d94ff; 

  /* Secondary Colors */
  --secondary-color: white; 
  --secondary-dark: #343a40; 
  --secondary-light: #bfc2c7; 

  /* Text Colors */
  --text-color: #212529; 
  --text-light: #ffffff; 
  --text-dark: #000000; 

  /* Accent Colors */
  --accent-color-1: #28a745; 
  --accent-color-2: #dc3545; 
  --accent-color-3: #ffc107; 

  /* Links */
  --link-color: #007bff; 

  /* button padding */
  --button-padding-none: 0px 0px;
  --button-padding-small: 3px 6px;
  --button-padding-medium: 6px 12px;
  --button-padding-large: 12px 24px;

  /* Border widths */
  --border-width-none: 0px;
  --border-width-xsmall: 1px;
  --border-width-small: 2px;
